from telethon import TelegramClient, events # pip install telethon   kodini terminalga tashlab oling avvalfrom config  import API_HASHd, API_IDd, bot_tokapi_id = API_IDdapi_hash = API_HASHdbot_token = bot_tokclient = TelegramClient('OcoderX', api_id, api_hash).start(bot_token=bot_token)@client.on(events.NewMessage(outgoing=True, pattern=r'[.!]nb'))async def nb_command(event):    await event.edit("Iltimos yigitlar ozroq kech qolaman nb qo ªymay tursin!")client.run_until_disconnected()